--
-- Page Heroes Production Import Script
-- This will insert or update all 25 page hero records
-- Run this on your PRODUCTION database
--

-- Insert/Update page heroes with conflict resolution
ON CONFLICT (page_path) DO UPDATE SET
  title = EXCLUDED.title,
  subtitle = EXCLUDED.subtitle,
  description = EXCLUDED.description,
  background_image_url = EXCLUDED.background_image_url,
  image_id = EXCLUDED.image_id,
  cta_text = EXCLUDED.cta_text,
  cta_link = EXCLUDED.cta_link,
  overlay_opacity = EXCLUDED.overlay_opacity,
  text_alignment = EXCLUDED.text_alignment,
  active = EXCLUDED.active,
  sort_order = EXCLUDED.sort_order,
  updated_at = NOW();

-- Verify the import
SELECT COUNT(*) as total_heroes FROM page_heroes;
SELECT page_path, title FROM page_heroes ORDER BY page_path;
