INSERT INTO public.team_members (id, slug, name, role, department, bio, avatar_image_id, email, phone, linkedin_url, twitter_url, sort_order, featured, active, created_at, updated_at, tags) VALUES ('81a89f45-a770-46b9-8564-ffc47379f444', 'bob-burden', 'Bob Burden', 'Chef', 'Culinary', 'Chef Bob brings decades of experience, culinary awards and talent to our communities. We will be seeing his influence in our menus, team training, resident experience, food presentation and an intentional infusion of our culinary program in all departments.', '5a73d1ce-cd30-435a-9f8b-71eb4525758c', 'info@goldenpond.com', '303-502-5554', NULL, NULL, 7, true, true, '2025-10-07 23:44:28.449697', '2025-10-07 23:44:28.449697', '["The Gardens at Columbine", "Golden Pond"]');
INSERT INTO public.team_members (id, slug, name, role, department, bio, avatar_image_id, email, phone, linkedin_url, twitter_url, sort_order, featured, active, created_at, updated_at, tags) VALUES ('59c81414-1d4f-42e0-bd3b-70c7bf911dc5', 'troy-mcclymonds', 'Troy McClymonds', 'Managing Partner', NULL, 'Troy has been a member of Stage Management, LLC since 2016, where he serves as a Managing Partner. He brings a wealth of entrepreneurial experience from the e-commerce and re-manufacturing sectors. As the founder and operator of a successful cell phone refurbishment company for over a decade, he developed exceptional skills in online advertising, automation, customer service, and process optimization—all critical elements in building a thriving enterprise. Often described as a "human Swiss Army knife," Troy demonstrates remarkable versatility across technical and interpersonal domains. His problem-solving abilities range from database troubleshooting to effective employee conflict resolution. Troy has seamlessly transferred these valuable skills to senior living management, where he excels in his current role. With comprehensive expertise in Yardi Systems, talent acquisition, and executive directorship, he leverages his diverse background to transform challenges into opportunities on a daily basis. Troy is also a proud member of the American Seniors Housing Association.', '9e91d14e-ebfb-4934-9b88-29b330f19cb6', 'troy@stagesenior.com', '303-667-6217', NULL, NULL, 0, false, true, '2025-10-09 05:38:58.525271', '2025-10-09 05:38:58.525271', '["Stage Management"]');
