INSERT INTO public.team_members (id, slug, name, role, department, bio, avatar_image_id, email, phone, linkedin_url, twitter_url, sort_order, featured, active, created_at, updated_at, tags) VALUES ('d3704b55-fa15-46b3-97c9-97cc78051407', 'leigh-boney', 'Leigh Boney', 'Executive Director', 'Leadership', 'Leigh Boney is the Executive Director of Golden Pond. She describes her role in senior care as deeply fulfilling, particularly through building connections with residents and their families. She has long wanted to run a retirement community and considers this her "dream job". She loves Golden Pond''s strong sense of community, describing it as feeling like an "extended family" where staff contributions are celebrated. Her background in organizational management and communication allows her to bring a "structured yet compassionate approach" to the team. Outside of work, Leigh enjoys gardening, horseback riding, and spending time outdoors with her husband and dogs. These activities help her maintain balance and bring a sense of calm to her professional role. A particularly memorable moment for her was organizing a family day event where she witnessed residents'' joy in connecting with loved ones, which reinforced her passion for senior care.', '2a05e4e8-5d48-4f9d-aa97-e1fec64d4ba1', 'lboney@stagesenior.com', '303-271-0430', NULL, NULL, 1, true, true, '2025-10-07 23:44:28.449697', '2025-10-07 23:44:28.449697', '[]');
INSERT INTO public.team_members (id, slug, name, role, department, bio, avatar_image_id, email, phone, linkedin_url, twitter_url, sort_order, featured, active, created_at, updated_at, tags) VALUES ('aeb30f78-b98e-43d1-8bdf-b6c93bed1c0d', 'allie-mitchem', 'Allie Mitchem', 'Memory Care Coordinator', NULL, 'The most fulfilling aspect of her role is building loving relationships with residents and their families, which enhances the quality of care each resident receives. She loves that her community is constantly innovating to improve residents'' quality of life, whether through new patio decor, an indoor garden, or a sensory room. Growing up raised by her grandparents, Allie never initially imagined working with elderly individuals. After losing her grandparents, she felt compelled to continue helping others. She has now worked in elder care for 12 years and considers her current role at Gardens her dream career. Outside of work, Allie is passionate about being a mother and wife. She feels fortunate to care for individuals with rich life experiences and hopes to learn from their wisdom. Her family enjoys activities like fishing, exploring, and engaging with animals.', 'fde37be8-b82d-4ec4-a26a-2494e30b254e', 'info@gardensatcolumbine.com', '303-973-5115', NULL, NULL, 0, false, true, '2025-10-09 05:39:02.808748', '2025-10-09 05:39:02.808748', '["Community Leadership", "The Gardens at Columbine"]');
