INSERT INTO public.team_members (id, slug, name, role, department, bio, avatar_image_id, email, phone, linkedin_url, twitter_url, sort_order, featured, active, created_at, updated_at, tags) VALUES ('56d72a5a-53b2-4e76-ab76-095f09a355e5', 'marci-gerke', 'Marci Gerke', 'Director of Memory Care Services', 'Care', 'Marci was initially brought to senior care by a friend who introduced her to the Gardens at Columbine. Despite expecting to work there briefly, she "fell in love with the residents and with the community and our team." About 10 years ago, her passion for Memory Care was sparked through conversations with families about residents needing specialized care. She is passionate about compassionate, person-centered care, particularly for those with dementia. She believes in avoiding traditional approaches that try to "reorient residents to reality," instead focusing on individual preferences and engagement. Outside work, she enjoys spending time with her 4 children and 3 grandsons, creating scrapbooks and cards, traveling, and focusing on her spiritual relationship.', '484190a2-b62f-40b9-823e-f710b06eb989', 'mgerke@stagesenior.com', '303-456-1500', NULL, NULL, 2, true, true, '2025-10-07 23:44:28.449697', '2025-10-07 23:44:28.449697', '["The Gardens at Columbine", "Golden Pond"]');
