INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('b38b86f7-26e8-4ea5-8062-6355bac37a40', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Wellness & Care', 'Comprehensive Health Services', 'Your health and wellness are our top priorities. Our on-site healthcare team provides 24/7 support, medication management, and coordination with your personal physicians. From routine check-ups to specialized care plans, we ensure you receive the attention and medical support you need to thrive.', NULL, 'Healthcare professional caring for a senior resident with compassion', 'Learn About Our Care', '#care', true, 1, true, '2025-09-26 20:16:15.591529', '2025-09-26 20:16:15.591529');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('3ce4bc54-f4ec-46f5-9775-ed27aa906f59', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Activities & Engagement', 'Vibrant Social Life', 'Every day brings new opportunities for connection and enjoyment. From morning fitness classes to evening entertainment, our activities calendar is filled with engaging programs. Join book clubs, art classes, gardening groups, or simply relax with friends in our beautiful common areas.', NULL, 'Seniors participating in engaging group activities and social events', 'See Activities Calendar', '#activities', false, 2, true, '2025-09-26 20:16:15.607277', '2025-09-26 20:16:15.607277');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('7e935abd-9bc3-482d-836b-5b262eb0313c', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Fine Dining', 'Extraordinary Culinary Experience', 'Enjoy chef-prepared meals in our elegant dining room. Our culinary team creates nutritious, delicious meals daily using fresh, locally-sourced ingredients. From hearty breakfasts to gourmet dinners, every meal is a celebration of flavor and nutrition tailored to your dietary needs.', '5ed6117a-63dd-4570-b130-c921e7e8dca2', 'Elegant dining room with seniors enjoying chef-prepared meals', 'View Sample Menus', '#dining', false, 0, true, '2025-09-26 20:16:15.533166', '2025-09-26 20:42:21.286');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('b6f8131d-d27b-4e9b-8a83-8626bdb33faf', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Care', 'Tailored Care for Your Changing Needs', 'Our full spectrum of support—from concierge physicians to on‑site rehabilitation—ensures you can remain at the same address even as your needs evolve.  Individualized care plans, 24/7 staff and adaptive services provide peace of mind.', NULL, NULL, NULL, NULL, false, 1, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('27c08a3f-023f-4d42-9054-3c4d23eb9d99', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Lifestyle', 'Captivating Activities & Events', 'From weekly clubs and creative workshops to fun outings and holiday celebrations, our life‑enrichment program sparks joy and connection for residents and families.', NULL, NULL, NULL, NULL, false, 2, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('14d61f33-051f-4c62-ad63-1af7e4053431', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Environment', 'Remarkable Setting & Amenities', 'Our five‑acre campus features landscaped courtyards, raised garden beds, walking paths, patios and water features that invite residents to stay active and connect with nature.  Indoors, enjoy the reading room, rehab center, salon, restaurant‑style dining and private dining rooms.', NULL, NULL, NULL, NULL, false, 3, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('a2d23309-9cd6-4d37-a48a-f722511cdfde', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Safety', 'Peace of Mind Technology', 'Discreet fall‑detection technology monitors safety events and provides rapid alerts without constant surveillance, giving residents independence while ensuring assistance is always close at hand.', NULL, NULL, NULL, NULL, false, 4, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('98fce90b-fc3d-40e3-8cd5-451425fa1f32', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Assisted Living', 'Personalized Care in a Comfortable Setting', 'Experience personalized care and daily support in a friendly environment. Our 24/7 nurse support and care staff provide assistance with daily living, medication management, and nutrition while respecting your independence. Enjoy three meals daily, social activities, fitness classes, and scheduled transportation—all included in one transparent monthly rate.', NULL, NULL, NULL, NULL, false, 1, true, '2025-10-09 20:34:16.677716', '2025-10-09 20:34:16.677716');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('253192c6-f192-4347-b38c-a87d28f759a7', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Memory Care', 'Compassionate Dementia Care', 'Our thoughtfully designed Memory Care neighborhood provides comfort, safety, and connection for residents living with Alzheimer''s or other dementias. With 25 private apartments, bright living areas, and a secure outdoor courtyard, residents thrive through personalized care, meaningful activities, and home-cooked meals. Our compassionate team is trained in current dementia-care methods and creates calm, engaging environments.', NULL, NULL, NULL, NULL, true, 2, true, '2025-10-09 20:34:16.677716', '2025-10-09 20:34:16.677716');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('b04670ac-4b9e-48d3-b9be-6836565d46a9', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Wellness', 'Active Living & Enrichment', 'Stay active and engaged with fitness classes 5 days a week, including "Stretch & Flex" and yoga. Enjoy our beautiful 3-acre garden with walkways, benches, and gazebo, plus raised flower beds for resident gardening. Our Life Enrichment team creates dynamic programs including professional entertainment, local outings, and social activities that nurture mind, body, and spirit.', NULL, NULL, NULL, NULL, false, 3, true, '2025-10-09 20:34:16.677716', '2025-10-09 20:34:16.677716');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('cb4f34ee-e44c-4032-b350-ebdfe03a684e', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Convenience', 'Worry-Free Living', 'Focus on what matters most with all-inclusive services: weekly housekeeping with linen service, daily bed making, building-wide Wi-Fi, all utilities including cable TV, free laundry rooms, and concierge service 7 days a week. On-site professional services include physician, podiatrist, dentist, audiologist, and hair salon.', NULL, NULL, NULL, NULL, true, 4, true, '2025-10-09 20:34:16.677716', '2025-10-09 20:34:16.677716');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('d4b1c7d0-4ca5-4963-bcda-c3bf3516ef50', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 'Since 2004', 'Proud Part of Golden, Colorado', 'Deeply connected to the community through partnerships with local schools, churches, and organizations. We honor Golden''s heritage with local artwork, historic photos, and Friday happy hours featuring Coors refreshments from our hometown brewery.', NULL, NULL, NULL, NULL, true, 1, true, '2025-10-09 21:47:17.442334', '2025-10-09 21:47:17.442334');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('3762a47b-c8c0-4c27-a542-505384dc6ec1', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', '98% Satisfaction', 'Your Forever Home', 'Most residents make Golden Pond their forever home. With care that grows with you — from independent living to assisted living and memory care — you can age in place with dignity and compassion.', NULL, NULL, NULL, NULL, false, 2, true, '2025-10-09 21:47:17.465547', '2025-10-09 21:47:17.465547');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('d9af7a22-6dce-4187-a086-95489951fc77', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 'Locally Owned', 'Upscale Yet Down-to-Earth', 'Experience the personal touch of local ownership. Responsive leadership, compassionate staff, and a family feel make all the difference. Life here is golden.', NULL, NULL, NULL, NULL, true, 3, true, '2025-10-09 21:47:17.483346', '2025-10-09 21:47:17.483346');
INSERT INTO public.community_features (id, community_id, eyebrow, title, body, image_id, image_alt, cta_label, cta_href, image_left, sort_order, active, created_at, updated_at) VALUES ('2a74733f-c00d-4c27-8af6-bdef932f4341', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 'Chef-Prepared', 'Restaurant-Style Dining', 'Seven days a week, enjoy fresh seasonal ingredients, weekly chef specials, and a la carte menus. Friday happy hours feature wine, beer, cocktails, and live entertainment.', NULL, NULL, NULL, NULL, false, 4, true, '2025-10-09 21:47:17.501383', '2025-10-09 21:47:17.501383');
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('ddb35c3c-b9f9-470b-9b00-29920304b0df', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 'Seamless Transitions', 'As your needs change, we''re here to support you every step of the way. Our unique continuum of care allows residents to transition smoothly between Assisted Living and Memory Care without leaving the community they call home.', NULL, 2, true, '2025-10-09 17:31:54.175438', '2025-10-09 17:31:54.175438', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('317a0a88-2e8a-4372-b93a-e5032ba8bc8c', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 'Engaging Activities & Programs', 'From fitness classes and creative arts to intergenerational programs and cultural outings, every day offers meaningful opportunities to stay active, socialize, and pursue your passions in a vibrant, supportive environment.', NULL, 3, true, '2025-10-09 17:31:54.175438', '2025-10-09 17:31:54.175438', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('7183cf10-77ed-4ecb-ae17-54ae3847a25e', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 'Connected to Arvada', 'We''re proud to be deeply rooted in the Arvada community. Through partnerships with local schools, nonprofits, and organizations, our residents remain active participants in the neighborhood, fostering connections that enrich daily life.', NULL, 4, true, '2025-10-09 17:31:54.175438', '2025-10-09 17:31:54.175438', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('abd2bbb2-06eb-45b3-a12f-dfbbd1382ada', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', '24/7 Care & Support', 'Our dedicated, licensed staff is available around the clock to provide personalized assistance with daily activities, medication management, and emergency response. With registered nurse oversight and individualized care plans, you''ll have peace of mind knowing professional care is always just moments away.', NULL, 1, true, '2025-10-09 17:31:54.175438', '2025-10-09 17:31:54.175438', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('70129750-6461-41b9-848b-313856987ffd', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 'Comprehensive Supportive Services', 'Access on-site medical services including concierge physicians, home health, audiology, podiatry, and hospice care. Our partnerships with healthcare providers ensure residents receive coordinated, convenient care without leaving the community.', NULL, 5, true, '2025-10-09 17:52:20.922798', '2025-10-09 17:52:20.922798', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('c96da256-33e1-4ea3-9f36-79ffdc798537', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 'Specialized Memory Care', 'Our secure memory care neighborhood features 27 private apartments designed specifically for residents with dementia and Alzheimer''s. With the "My Story" intake tool, we create deeply personalized care plans, and our courtyard with heated paths provides a safe outdoor space for enjoying nature year-round.', NULL, 6, true, '2025-10-09 17:52:20.942226', '2025-10-09 17:52:20.942226', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('e05a19b1-917e-4e7c-974f-a0c76acf64f0', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Trusted Since 2000', 'Over two decades of locally owned, family-centered care in Littleton. Many staff members have been here since day one — that experience and commitment make The Gardens feel like home.', NULL, 1, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('02c7344c-15af-4032-9f8b-e828592744d9', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Three Acres of Botanic Gardens', 'Stroll winding garden paths lined with benches, a gazebo, raised flower beds, and seasonal blooms. Our outdoor sanctuary includes two secure courtyards for memory care residents with garden paths and covered patios.', NULL, 2, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('ed244d2f-2d27-4779-8c76-53e03e15e07b', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Comprehensive Care Continuum', 'From Assisted Living to specialized Memory Care, residents can age in place with confidence. On-site professionals include physicians, podiatrists, dentists, audiologists, and therapy services — plus 24/7 nurse support.', NULL, 3, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('be27f9b6-8555-4811-87b2-8fab7baaffce', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Restaurant-Style Dining', 'Executive chef-prepared meals with fresh, seasonal ingredients. Enjoy daily specials, happy hour every Friday, and a full à la carte menu. Monthly food committee meetings ensure your favorites are always available.', NULL, 4, true, '2025-10-09 18:09:43.043541', '2025-10-09 18:09:43.043541', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('d8b1d92e-ca86-46aa-a97a-4eb5492ee2fd', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Specialized Memory Care', 'Compassionate dementia care with the "My Story" program, individualized care plans, and engaging activities like music therapy, art programs, and sensory rooms. Two secure courtyards offer safe outdoor experiences.', NULL, 5, true, '2025-10-09 18:09:43.043541', '2025-10-09 18:09:43.043541', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('bad752b4-44dc-4e61-8729-e60a88c9b1fc', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 'Active, Engaged Lifestyle', 'Fitness classes five days a week, scenic drives, creative arts, intergenerational programs, and community outings. Our life enrichment philosophy: Be Active. Be Healthy. Be Happy.', NULL, 6, true, '2025-10-09 18:09:43.043541', '2025-10-09 18:09:43.043541', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('0ce42913-8802-4af4-bb2f-07086e2b3558', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Peaceful Mountain Setting', 'Surrounded by breathtaking mountain views of Arvada, Colorado, Stonebridge offers a tranquil environment connected to the vibrant local community through partnerships with schools, churches, and non-profits.', NULL, 1, true, '2025-10-09 20:34:48.03522', '2025-10-09 20:34:48.03522', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('71c8b1ab-78e7-4716-8c1a-bc3901aa8d78', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', '24/7 Compassionate Care', 'Round-the-clock nurse support and care staff provide help with daily living, medication management, nutrition, and housekeeping—ensuring smooth transitions to higher levels of care when needed.', NULL, 2, true, '2025-10-09 20:34:48.03522', '2025-10-09 20:34:48.03522', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('9f554557-4720-4d36-ad49-0e38f503e029', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Beautiful Outdoor Spaces', 'Enjoy our 3-acre garden with walkways, benches, and gazebo, plus raised flower beds for gardening. Relax on our scenic outdoor patio and second-floor sun deck with mountain views.', NULL, 3, true, '2025-10-09 20:34:48.03522', '2025-10-09 20:34:48.03522', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('2a1be480-cfb0-439f-bdc4-af3c28545ec4', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Engaging Activities & Fitness', 'Stay active with fitness classes 5 days a week, professional entertainment, local outings, and social activities. Our gym, library retreat, and activity programs keep residents engaged.', NULL, 4, true, '2025-10-09 20:34:48.03522', '2025-10-09 20:34:48.03522', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('52498d3c-955c-4b86-8ea7-ab03abc5b643', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Family-Style Dining', 'Enjoy three chef-prepared meals daily in our lively dining room or connect in our private dining space. Plus, visit our full-service café anytime for snacks and refreshments.', NULL, 5, true, '2025-10-09 20:34:48.03522', '2025-10-09 20:34:48.03522', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('b4485dd6-7db6-4349-adcd-764fc48b4cbf', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 'Exceptional Memory Care Team', 'Led by Regional Director Marci Gerke, our tenured team brings years of personalized experience with ongoing dementia training and advanced care methods. We believe in the "My Story" program that captures each resident''s personal history.', NULL, 6, true, '2025-10-09 20:34:48.03522', '2025-10-09 20:34:48.03522', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('7c3c0972-24c1-4d2e-957e-dc1f776e576c', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', '98% Resident Satisfaction', 'Families know what we know: life here is golden.', NULL, 1, true, '2025-10-09 21:47:17.545525', '2025-10-09 21:47:17.545525', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('46e758d4-0987-4c34-97f5-9e1df7370064', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 'Locally Owned Since 2004', 'Personal touch, responsive leadership, family feel.', NULL, 2, true, '2025-10-09 21:47:17.570348', '2025-10-09 21:47:17.570348', NULL, NULL);
INSERT INTO public.community_highlights (id, community_id, title, description, image_id, sort_order, active, created_at, updated_at, cta_label, cta_href) VALUES ('8a308b5b-38d8-4460-97f5-32dc42700f01', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 'Care That Grows With You', 'Independent, assisted, and memory care options.', NULL, 3, true, '2025-10-09 21:47:17.58777', '2025-10-09 21:47:17.58777', NULL, NULL);
