INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('530ea02d-ea0d-4792-ac4f-5dfbce046d8c', 'What care levels do you offer?', 'Our communities offer Assisted Living and/or Memory Care; some also provide Independent Living Plus. Please check each community page for specifics.', 'Care', NULL, 1, true, '2025-09-24 04:16:16.847764', '2025-09-24 04:16:16.847764', 'Our communities offer Assisted Living and/or Memory Care; some also provide Independent Living Plus. Please check each community page for specifics.', 1);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('f08016f7-fc8b-4097-8251-8ee7dcd754c7', 'Can residents age in place?', 'Yes. Many residents transition between care levels within the same community as needs change, minimizing disruption.', 'Care', NULL, 2, true, '2025-09-24 04:16:16.865951', '2025-09-24 04:16:16.865951', 'Yes. Many residents transition between care levels within the same community as needs change, minimizing disruption.', 2);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('874aa338-2384-4a4d-b8b0-752e65f5de5e', 'Are pets allowed?', 'Yes—select communities are pet friendly. Please ask about any breed or care considerations.', 'Lifestyle', NULL, 3, true, '2025-09-24 04:16:16.881399', '2025-09-24 04:16:16.881399', 'Yes—select communities are pet friendly. Please ask about any breed or care considerations.', 3);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('77b4efa4-96e9-46b2-a448-bacf7818ee79', 'What unique amenities are on campus?', 'Our heated courtyard has a working train set, plus a greenhouse, putting green, fitness room, salon, and chapel.', 'Amenities', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 1, true, '2025-09-24 04:16:16.944837', '2025-09-24 04:16:16.944837', 'Our heated courtyard has a working train set, plus a greenhouse, putting green, fitness room, salon, and chapel.', 1);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('0c8f7b43-ea82-4df7-8686-ed7c261496de', 'Is Memory Care secure?', 'Yes. Stonebridge provides a secure Memory Care neighborhood with dedicated programming and courtyards.', 'Care', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 1, true, '2025-09-24 04:16:17.01606', '2025-09-24 04:16:17.01606', 'Yes. Stonebridge provides a secure Memory Care neighborhood with dedicated programming and courtyards.', 1);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('8b20835f-a885-4267-8fb5-74491929b65b', 'Is there outdoor garden space?', 'Yes. The Gardens at Columbine features expansive landscaped gardens, walking paths, and peaceful courtyards.', 'Outdoors', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 1, true, '2025-09-24 04:16:16.913417', '2025-09-24 04:16:16.913417', 'Yes. The Gardens at Columbine features expansive landscaped gardens, walking paths, and peaceful courtyards.', 1);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('cf088c16-d223-463a-b1ce-2d13bbf90262', 'Where exactly are you, and how easy is it to visit?', 'We''re at 5130 W Ken Caryl Ave in Littleton—about 10 minutes to Downtown Littleton and 15 miles to Downtown Denver, with quick access to Santa Fe Dr and C-470. Tours can be in-person or virtual.', 'Location & Visiting', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 1, true, '2025-09-24 19:56:05.458102', '2025-09-24 19:56:05.458102', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('3efa0563-2110-42fd-9359-5f679f404c34', 'What living options do you actually have?', 'Assisted Living and a secure Memory Care neighborhood (122 total apartments: 96 AL / 26 MC). If needs change, you don''t have to move across town—we handle the transition here.', 'Care Levels', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 2, true, '2025-09-24 19:56:05.590654', '2025-09-24 19:56:05.590654', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('c060c030-b786-4a3a-a672-a4179d67d78c', 'What kind of community is this, and where?', 'An upscale Assisted Living & Memory Care community at 6447 Quail St in Arvada, minutes from Olde Town—quiet neighborhood, easy access for family.', 'Location & Overview', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 1, true, '2025-09-24 19:56:11.135463', '2025-09-24 19:56:11.135463', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('d1558757-d9d9-4306-bfba-d6c0913b1a28', 'What will my mom''s day look like?', 'Clubs, art therapy, pet interaction, live music, and Memory-Care-specific engagement (balloon volleyball, hallway bowling)—plus seasonal events (luau, craft fairs).', 'Daily Life & Activities', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 2, true, '2025-09-24 19:56:11.271439', '2025-09-24 19:56:11.271439', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('b76a0b13-495a-4a8c-b02a-055ebc25d650', 'Is it pet-friendly?', 'Yes—bring the furry friend. (Respite guests can, too.)', 'Pet Policy', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 3, true, '2025-09-24 19:56:11.392935', '2025-09-24 19:56:11.392935', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('144298c7-4e1d-4d6e-b5bf-d744cbf01b13', 'Any standout amenities we should know about?', 'Heated courtyard with a working train set, greenhouse, putting green, salon, workout center, library, theater—designed to feel lively and welcoming.', 'Amenities', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 4, true, '2025-09-24 19:56:11.526066', '2025-09-24 19:56:11.526066', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('3278a029-cc56-4533-a215-9276ecfa2989', 'What if care needs change?', 'We coordinate seamless transitions inside the community; staff and directors stay closely involved. Concierge physicians and on-site allied health providers visit regularly.', 'Care Transitions', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 5, true, '2025-09-24 19:56:11.650312', '2025-09-24 19:56:11.650312', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('2ce5f119-3611-4bc1-ab0e-5bc4c1e957c7', 'Short-term or respite stays while we figure things out?', 'Yes—two-week minimum respite stays with full access to activities and amenities.', 'Respite Care', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 6, true, '2025-09-24 19:56:11.779365', '2025-09-24 19:56:11.779365', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('61a5a60c-c0d3-4e88-910a-328c71a93a0d', 'Spiritual care?', 'On-site chapel support plus Stage Senior''s Chaplain Program (optional, all faiths welcome).', 'Spiritual Care', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 7, true, '2025-09-24 19:56:11.91103', '2025-09-24 19:56:11.91103', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('0df17367-3312-4939-9f64-6d7033746d6f', 'Dining—can she eat in her suite if needed?', 'Chef-crafted, restaurant-style meals daily; in-suite delivery is available. (Families often join for a meal during tours.)', 'Dining', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 8, true, '2025-09-24 19:56:12.032667', '2025-09-24 19:56:12.032667', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('34a51f76-66a3-41bf-911f-61ca7099d60a', 'New to Arvada? Where should we start?', 'See our Arvada Senior Guide for local healthcare, activities, transportation, and more. For dementia education and 24/7 helpline (800-272-3900), tap the Alzheimer''s Association Colorado.', 'Local Resources', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 9, true, '2025-09-24 19:56:12.162293', '2025-09-24 19:56:12.162293', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('3999b7b8-985a-452c-862d-0994f3433ac2', 'Need a decision framework you can print?', 'Download the Decision Guide (care level quiz, comparison sheets, visit checklist).', 'Resources & Tools', 'b2c48ce7-11cb-4216-afdb-f2429ccae81f', 10, true, '2025-09-24 19:56:12.284178', '2025-09-24 19:56:12.284178', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('9553d6d8-da5e-49b3-bb4b-e71f76a603a0', 'What makes Stonebridge feel different?', 'Your Story First®: before services or schedules, we learn the resident''s routines, history, and family traditions, then tailor care (and keep updating it with you).', 'Our Approach', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 1, true, '2025-09-24 19:56:12.405183', '2025-09-24 19:56:12.405183', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('d116370b-a288-421c-b9d9-07eeb6179ff3', 'Where are you and how do we visit?', '11825 W 64th Ave, Arvada. Same-day and next-day tours are common—call or schedule online.', 'Location & Tours', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 2, true, '2025-09-24 19:56:12.525246', '2025-09-24 19:56:12.525246', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('9d472dba-1cfb-4dc4-8fe4-29875e4ced02', 'What care levels do you offer?', 'Assisted Living and secure Memory Care, with 24/7 trained staff and an activities program that adapts to abilities.', 'Care Levels', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 3, true, '2025-09-24 19:56:12.658528', '2025-09-24 19:56:12.658528', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('b4aa0333-05fa-4b5e-834d-9de2e835c03c', 'What''s daily life like?', 'Three chef-prepared meals, clubs and outings, music and exercise, garden time, faith services, and family events. See the activities calendar and sample menu anytime.', 'Daily Life', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 4, true, '2025-09-24 19:56:12.787752', '2025-09-24 19:56:12.787752', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('99c21b39-b4a7-410b-b46b-703c7185ea01', 'Dietary needs and family meals?', 'We support special diets; there''s a private dining room for family gatherings and celebrations.', 'Dining', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 5, true, '2025-09-24 19:56:12.910514', '2025-09-24 19:56:12.910514', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('69dc21b7-b60a-4959-898f-7d233b1cd426', 'Medical support and emergencies?', 'We coordinate visiting physicians and allied health (PT/OT/speech, podiatry, mobile dental, pharmacy). Apartments have emergency response; staff are on-site 24/7 with established protocols.', 'Medical & Healthcare', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 6, true, '2025-09-24 19:56:13.031286', '2025-09-24 19:56:13.031286', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('4d3b2524-1457-4bb8-8d89-109101c7de05', 'Is Stonebridge pet-friendly?', 'Ask the team about current pet guidelines; many families choose Stonebridge because it balances home-like warmth with safety. (Our testimonials page gives a feel for the culture.)', 'Pet Policy', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 7, true, '2025-09-24 19:56:13.163001', '2025-09-24 19:56:13.163001', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('89bb5e90-006a-40f6-b279-8c7f8ea01bd1', 'Spiritual support?', 'Stage Senior''s optional Chaplain Program is available across our communities for residents, families, and staff.', 'Spiritual Care', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 8, true, '2025-09-24 19:56:13.285456', '2025-09-24 19:56:13.285456', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('116eb7ce-8654-4d29-adf4-9594e6cabbb7', 'New to Arvada? What resources help families most?', 'Start with our Local Area Guide and the Arvada Senior Guide (care, transportation, activities). For dementia education and a 24/7 helpline, use the Alzheimer''s Association Colorado chapter.', 'Local Resources', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 9, true, '2025-09-24 19:56:13.406641', '2025-09-24 19:56:13.406641', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('20ba814c-65ac-4648-8672-80b7e5059970', 'Transportation for appointments?', 'Yes—scheduled transportation for medical visits and local errands is included.', 'Transportation', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 6, true, '2025-09-24 19:56:09.900169', '2025-09-24 19:56:09.900169', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('12c95a64-ad02-4b4b-80f7-50fc15af1268', 'Finances—any benefits we should look into?', 'If the resident is a wartime veteran or surviving spouse, ask your advisor about VA Pension with Aid & Attendance; it can offset care costs for those who qualify. (We can''t determine eligibility, but these are the official starting points.)', 'Financial Assistance', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 10, true, '2025-09-24 19:56:13.526641', '2025-09-24 19:56:13.526641', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('78c1ae9b-72a0-4470-bc95-7acfd924024d', 'How ''homey'' does it feel day-to-day?', 'Expect a true garden campus—private courtyards, walking paths, and patio spaces—plus a calendar built for connection (Happy Hour Fridays, family brunches, holiday events).', 'Living Experience', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 3, true, '2025-09-24 19:56:06.212686', '2025-09-24 19:56:06.212686', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('a1c6ffd0-0cda-4314-9d1f-04a59dbce17a', 'Can Mom keep her doctor? What medical help comes in?', 'Yes—families often keep primary physicians. We also coordinate concierge physicians and on-site rehab/therapies to reduce outside trips.', 'Medical & Healthcare', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 4, true, '2025-09-24 19:56:08.437863', '2025-09-24 19:56:08.437863', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('31bc381b-8982-480c-92f2-cc60db0584b9', 'Is dining flexible? Can we share a meal during a tour?', 'Restaurant-style meals daily with in-room delivery on request—and we regularly host prospective families for a complimentary lunch during tours.', 'Dining', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 5, true, '2025-09-24 19:56:09.388579', '2025-09-24 19:56:09.388579', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('d7f81a18-16ed-4ad6-b09f-4eb5f3c88258', 'Pets—can Dad bring his dog?', 'We''re pet-friendly; residents routinely enjoy pet programs and visits.', 'Pet Policy', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 7, true, '2025-09-24 19:56:10.443399', '2025-09-24 19:56:10.443399', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('7044c7b4-e991-4b54-8b5b-5ae2f0784ce6', 'Spiritual support available?', 'Yes. Beyond local faith options, Stage Senior offers an optional Chaplain Program via Senior Living Chaplains for residents, families, and staff.', 'Spiritual Care', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 8, true, '2025-09-24 19:56:10.769566', '2025-09-24 19:56:10.769566', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('eba5e21f-f150-4ee6-a047-eb7a623c14c2', 'New to Littleton? What''s nearby for seniors and caregivers?', 'Use our Littleton Senior Guide for local healthcare, transportation (RTD Access-a-Ride), activities, and caregiver support. For dementia support and classes, the Alzheimer''s Association Colorado chapter is excellent.', 'Local Resources', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 9, true, '2025-09-24 19:56:10.889739', '2025-09-24 19:56:10.889739', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('0596353f-1030-425e-9295-84d06eb6466c', 'How do we take the next step—today?', 'Grab a virtual tour or book a visit; we''ll talk apartment types, current availability, and a personalized care plan.', 'Getting Started', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 10, true, '2025-09-24 19:56:11.013997', '2025-09-24 19:56:11.013997', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('64975583-660a-4243-a8b7-5d8f085ec67c', 'What is included in the monthly rent?', 'Monthly rent covers chef‑prepared meals, room service, coffee and snack stations, weekly housekeeping and linen service, personal laundry, all utilities except telephone, DirectTV high‑definition programming, high‑speed Wi‑Fi, complimentary washers and dryers, maintenance services, individual climate control and access to licensed care staff and emergency response systems.', 'Pricing', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 1, true, '2025-10-08 00:34:26.524361', '2025-10-08 00:34:26.524361', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('4c2c7763-0c74-485f-9d2a-6ec5ec3eca61', 'What types of activities are offered?', 'Stonebridge offers arts and culture programs (art classes, creative writing, music performances), wellness classes (exercise, yoga, walking club, dance fitness), educational workshops (guest lectures, tech tutorials), nature outings (gardening club, trishaw rides, scenic trips) and games such as trivia, bingo and board games.  Seasonal festivals, holiday celebrations, local excursions and monthly themed dinners round out the calendar.', 'Activities', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 2, true, '2025-10-08 00:34:26.524361', '2025-10-08 00:34:26.524361', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('60ce4825-f896-4dcc-a193-f0b4678918e1', 'Do you provide specialized memory care?', 'Yes.  The Meadows at Stonebridge offers secure apartments with around‑the‑clock support, personalized care plans and engaging activities such as art therapy, music and gentle exercise.  Memory‑care residents receive the same quality dining as assisted‑living residents and families are encouraged to participate in care planning.', 'Care Options', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 3, true, '2025-10-08 00:34:26.524361', '2025-10-08 00:34:26.524361', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('4ecd1f51-f8d0-4f3f-b791-cdf47d1b40a4', 'How do I schedule a tour?', 'Tours are available seven days a week.  You can schedule a tour by visiting our contact page or calling (720) 729‑6244 to speak with a care advisor.', 'Tours', 'd20c45d3-8201-476a-aeb3-9b941f717ccf', 4, true, '2025-10-08 00:34:26.524361', '2025-10-08 00:34:26.524361', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('56738a75-a205-453b-9ad2-595893c89513', 'What safety measures are in place?', 'Every apartment is equipped with an emergency pull‑cord or pendant system monitored 24/7, and the community has discreet fall‑detection technology for quick response.', 'Safety', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 1, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('195ab65a-ae3e-47c9-a801-e61316494857', 'Are pets allowed?', 'Yes! The Gardens at Columbine welcomes small pets and provides amenities that make it easy to bring your furry friend to your new home.', 'Lifestyle', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 2, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('76c57c40-4d2b-4ca1-832d-905a37dce8a3', 'What dining options are available?', 'Residents enjoy a restaurant‑style dining room with à‑la‑carte selections, daily specials and scratch‑made soups.  Room service is available, and wine nights and holiday menus add variety.', 'Dining', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 3, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('23019da6-4f4c-4036-826a-6a464c132094', 'Does the community provide transportation?', 'Yes. Complimentary scheduled rides are available for medical appointments along with weekly shuttles to shops, banks, churches and restaurants.', 'Services', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 4, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('7229ea26-fd86-4626-9ec5-f99e7058ab07', 'What activities are offered?', 'Programs include creative writing, men''s club, Bible studies, art classes, gardening club, walking club, bingo, live music, fitness classes, scenic outings and holiday celebrations.', 'Lifestyle', 'dea2cbbe-32da-4774-a85b-5dd9286892ed', 5, true, '2025-10-08 00:34:27.668953', '2025-10-08 00:34:27.668953', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('ad5bdd4b-c023-43ce-bb02-63f3c4c1321b', 'What is included in Independent Living?', 'Newly remodeled apartments with granite countertops, stainless appliances, tile backsplash, in-unit washer/dryer, community-wide Wi-Fi, Comcast HD cable, free transportation to doctor appointments, on-site dining with three chef-prepared meals daily, bi-weekly housekeeping and linen service, all utilities except telephone, daily trash service, and 24-hour emergency monitoring.', 'Independent Living', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 1, true, '2025-10-09 21:47:17.821664', '2025-10-09 21:47:17.821664', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('359af5ff-ba77-455c-a4b9-c379891697e9', 'What is included in Assisted Living?', '24/7 care staff and RN oversight, individualized care plans, three meals daily, weekly housekeeping and linen service, all utilities (electricity, water, trash, cable, phone), daily trash service, free laundry rooms or staff laundry option, Wi-Fi and Comcast HD cable, 24-hour emergency system with optional pendant, on-site medical services, and scheduled transportation.', 'Assisted Living', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 2, true, '2025-10-09 21:47:17.842133', '2025-10-09 21:47:17.842133', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('0d1fc0a9-5aa1-4fe3-9fbb-e314f7ea5d9f', 'What is The Meadows?', 'The Meadows is our specialized, secure memory care neighborhood with 15 private studios, registered nurse oversight, secured courtyard with mountain views, full activities calendar, inclusive care up to 150 points, weekly housekeeping and laundry, and newly remodeled dining and activity areas.', 'Memory Care', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 3, true, '2025-10-09 21:47:17.859415', '2025-10-09 21:47:17.859415', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('384595e8-aed4-4c35-9b33-f2e95fc7f326', 'How much does it cost?', 'Independent Living starts at $4,600/month for one-bedroom apartments. Assisted Living starts at $5,900/month for studios. Memory Care at The Meadows is $10,000/month for private studios. All options include a one-time community fee of $5,000.', 'Pricing', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 4, true, '2025-10-09 21:47:17.876672', '2025-10-09 21:47:17.876672', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('db6f4c41-6cc0-4fd2-bd0d-a006596bf120', 'What are Care Points?', 'In Assisted Living, care needs are evaluated before move-in. Each Care Point equals $20 per month, added to base rent according to assessed needs. Memory Care includes up to 150 Care Points; beyond that, each additional point is $20/month.', 'Pricing', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 5, true, '2025-10-09 21:47:17.893816', '2025-10-09 21:47:17.893816', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('de0642a2-94f9-40d6-8581-54deaabe4a50', 'What dining options are available?', 'Restaurant-style dining seven days a week with breakfast (7–9 a.m.), lunch (11 a.m.–1 p.m.), and dinner (4–6 p.m.). Assisted Living and Memory Care include all meals. Independent Living residents can purchase meal plans: breakfast $10, lunch $17, dinner $17. Friday happy hour runs 4–5 p.m. with wine, beer, and cocktails.', 'Dining', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 6, true, '2025-10-09 21:47:17.911041', '2025-10-09 21:47:17.911041', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('bbde7c51-fb0d-4e67-94b0-b8863f5861dd', 'What activities are offered?', 'Scenic drives, discussion groups, guest speakers, music and painting classes, bingo, games, holiday and family events, religious programs, walking and exercise groups, knitting and hobby circles, creative workshops, intergenerational activities, resident council, and memory-specific activities like art program, music therapy, hallway bowling, and goat yoga.', 'Activities', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 7, true, '2025-10-09 21:47:17.929038', '2025-10-09 21:47:17.929038', NULL, 0);
INSERT INTO public.faqs (id, question, answer, category, community_id, sort_order, active, created_at, updated_at, answer_html, sort) VALUES ('25f32a97-384c-44ff-aa4b-51cf0004138f', 'Tell me about Golden, Colorado.', 'Golden was founded during the 1859 gold rush along Clear Creek and served as capital of the Colorado Territory from 1862–1867. It''s home to the world''s largest single brewery (Coors) and is surrounded by foothills, trails, and open space. Today it has 45,000 residents and a thriving small-town culture.', 'Community', 'ac9dae44-d18f-4e96-b3bf-ba3d4fc406e9', 8, true, '2025-10-09 21:47:17.94615', '2025-10-09 21:47:17.94615', NULL, 0);
